@import "@/styles/global-color.scss";

$dark_gray: #889aa4;
$light_gray: rgba(0, 0, 0, 0.1);

.login-container {
  min-height: 100%;
  width: 100%;
  min-width: 341px;
  background-color: $app-background;
  overflow: hidden;

  .form-container {
    background-color: $color-white;
    width: 100%;
    max-width: 322px;
    margin: 0 auto;
    border: solid 1px $app-background;
    -webkit-border-radius: 5px;
    border-radius: 5px;
    padding: 20px;
    font-size: 1rem;
    position: relative;
  }

  .login-form {
    position: relative;
    width: 520px;
    max-width: 100%;
    padding: 100px 35px 0;
    margin: 0 auto;
    overflow: hidden;
  }

  .tips {
    font-size: 14px;
    margin-bottom: 10px;

    span {
      &:first-of-type {
        margin-right: 16px;
      }
    }
  }

  .svg-container {
    padding: 6px 5px 6px 15px;
    color: $dark_gray;
    vertical-align: middle;
    width: 30px;
    display: inline-block;
  }

  .title-container {
    position: relative;

    .logo {
      position: relative;
      max-width: 250px;
      margin-bottom: 20px;
    }
  }

  .show-pwd {
    position: absolute;
    right: 10px;
    top: 7px;
    font-size: 16px;
    color: $color-black;
    cursor: pointer;
    user-select: none;
  }
}
